$(window).resize(function () {
    if ($(window).width() <= 992) {
        $('.footer .copy').insertAfter('.footer .row');
        if ($('.collapse').hasClass('active')) {
            $(".overlay").addClass('active');
        }
    } else {
        $('.footer .copy').insertAfter('.footer .footer-logo ');
        $(".overlay").removeClass('active');
    }
});
$(window).resize();


$(document).ready(function () {

    $('.reviews-slider').slick({
        arrows: false,
        dots: true,
        infinite: true,
        slidesToShow: 1,
        slidesToScroll: 1
    });

    $('.file-input').on('change', function (e) {
        var fileName = e.target.files[0].name;
        $('.file-name').addClass("selected").html(fileName);

    });

    $('.why-chose-slider').slick({
        arrows: false,
        dots: false,
        infinite: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        responsive: [
            {
                breakpoint: 991,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                    infinite: true,
                    dots: true
                }
            },
            {
                breakpoint: 576,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                    dots: true
                }
            }
        ]
    });

    $('#slide-submenu').on('click', function () {
        $('.sidebar').toggleClass('active');
        $('.mini-submenu').show();
        $('#slide-submenu').toggleClass('active');
        if ($('.collapse').hasClass('active')) {
            return false;
        } else {
            $("body").removeClass("overflow-hidden");
        }
    });

    $('.profile-slider').slick({
        arrows: true,
        dots: false,
        infinite: false,
        slidesToShow: 6,
        variableWidth: true
    });

    var input = $("#phone"),
        output = $("#output");
    input.intlTelInput({
        separateDialCode: true,
        nationalMode: true,
        // utilsScript: "../../js/utils.js"
    });

    $('.mini-submenu').on('click', function () {
        $('.sidebar').toggleClass('active');
        $('.mini-submenu').hide('slow');
        $('#slide-submenu').toggleClass('active');
        $("body").addClass("overflow-hidden");
    });
    $('.navbar-toggler').on('click', function () {
        $(".collapse").toggleClass("active");
        $(".overlay").toggleClass("active")
        $(".navbar-toggler-icon").toggleClass("active");
        $("body").toggleClass("overflow-hidden");
    });
    $('.overlay').on('click', function () {
        $('.navbar-toggler').click();
    });


    $('.profile-slider input').on('click', function () {
        $('.user-wrap').removeClass('checked');
        $(this).closest('.user-wrap').addClass('checked')
    });


    // show hide addishinal info on mobile pages user page
    $('.show-more-info').on('click', function (event) {
        event.preventDefault();
        if ($(this).hasClass('hide')) {
            $(this).find('.text').text('Hide more information');
        } else {
            $(this).find('.text').text('Show more information');

        }
        $('.user-hide-sm').toggleClass('hide');
        $(this).toggleClass('hide');
    });

    // clean and close popup when click cancel button
    $('.btn-cancel').on('click', function (event) {
        event.preventDefault();
        $(this).closest('form').find("input[type=text], textarea, input[type=file], input[type=email], input[type=checkbox]").val("");
        $("option:selected").prop("selected", false);
        $('.file-name').removeClass("selected");
    });
    // action click +add
    $('.btn-add').on('click', function (event) {
        event.preventDefault();
    });

});

$('#year').text(new Date().getFullYear());
